def HOUSE_DETAIL = null;
def UNIT_DETAIL = null;
def STREET_DETAIL = null;
def SUBURB_DETAIL = null;
def LOCALITY_DETAIL = null;
def POSTCODE_DETAIL = null;
def UNIT_TYPE = null;
def HABITATION_NAME = null;
def LEVEL_DETAIL = null;
// If Unit Identifier Low is different to Unit Identifier High
if(ctx['UNIT_IDENTIFIER_LOW'] != null && ctx['UNIT_IDENTIFIER_HIGH'] != null && ctx['UNIT_IDENTIFIER_LOW'] != ctx['UNIT_IDENTIFIER_HIGH']) {
  UNIT_DETAIL = ctx['UNIT_IDENTIFIER_LOW'] + '-' + ctx['UNIT_IDENTIFIER_HIGH'];
}
// If Unit Identifier Low and High are the same then
if(ctx['UNIT_IDENTIFIER_LOW'] != null && ctx['UNIT_IDENTIFIER_LOW'] == ctx['UNIT_IDENTIFIER_HIGH']) {
  UNIT_DETAIL = ctx['UNIT_IDENTIFIER_LOW'];
}
// If House Low is same as House High and House Low Suffix is null and House High Suffix is null
if(ctx['HOUSE_LOW'] != null && ctx['HOUSE_LOW'] == ctx['HOUSE_HIGH'] && ctx['HOUSE_LOW_SUFFIX'] == null && ctx['HOUSE_HIGH_SUFFIX'] == null) {
  HOUSE_DETAIL = ctx['HOUSE_LOW'];
}
// If House Low is different to House High and House Low Suffix is null and House High Suffix is null
if(ctx['HOUSE_LOW'] != null && ctx['HOUSE_HIGH'] != null && ctx['HOUSE_LOW'] != ctx['HOUSE_HIGH'] && ctx['HOUSE_LOW_SUFFIX'] == null && ctx['HOUSE_HIGH_SUFFIX'] == null) {
  HOUSE_DETAIL = ctx['HOUSE_LOW'] + '-' + ctx['HOUSE_HIGH'];
}
// If House Low is same as House High and House Low Suffix is same as House High Suffix
if(ctx['HOUSE_LOW'] != null && ctx['HOUSE_LOW'] == ctx['HOUSE_HIGH'] && ctx['HOUSE_LOW_SUFFIX'] != null && ctx['HOUSE_LOW_SUFFIX'] == ctx['HOUSE_HIGH_SUFFIX']) {
  HOUSE_DETAIL = ctx['HOUSE_LOW'] + ctx['HOUSE_LOW_SUFFIX'];
}
// If House Low is same as House High and House Low Suffix is different to House High Suffix
if(ctx['HOUSE_LOW'] != null && ctx['HOUSE_LOW_SUFFIX'] != null && ctx['HOUSE_HIGH'] != null && ctx['HOUSE_HIGH_SUFFIX'] != null && ctx['HOUSE_LOW'] == ctx['HOUSE_HIGH'] && ctx['HOUSE_LOW_SUFFIX'] != ctx['HOUSE_HIGH_SUFFIX']) {
  HOUSE_DETAIL = ctx['HOUSE_LOW'] + ctx['HOUSE_LOW_SUFFIX'] + '-' + ctx['HOUSE_HIGH'] + ctx['HOUSE_HIGH_SUFFIX'];
}
// If House Low is different to House High and House Low Suffix is different to House High Suffix
if(ctx['HOUSE_LOW'] != null && ctx['HOUSE_LOW_SUFFIX'] != null && ctx['HOUSE_HIGH'] != null && ctx['HOUSE_HIGH_SUFFIX'] != null && ctx['HOUSE_LOW'] != ctx['HOUSE_HIGH'] && ctx['HOUSE_LOW_SUFFIX'] != ctx['HOUSE_HIGH_SUFFIX']) {
  HOUSE_DETAIL = ctx['HOUSE_LOW'] + ctx['HOUSE_LOW_SUFFIX'] + '-' + ctx['HOUSE_HIGH'] + ctx['HOUSE_HIGH_SUFFIX'];
}
// Street Detail consists of Street Name, Street Type and Street Direction, with space between (and trimmed) e.g. if Street Direction is null then the space is not required after Street Type
if(ctx['STREET_DIRECTION'] == null) {
  STREET_DETAIL = ctx['STREET_NAME'] + ' ' + ctx['STREET_TYPE'];
} else {
  STREET_DETAIL = ctx['STREET_NAME'] + ' ' + ctx['STREET_TYPE'] + ' ' + ctx['STREET_DIRECTION'];
}
// If Locality_type is SUBURB and the City_locality isnt the same then use the SUBURB field else SUBURB_DETAIL should be blank. 
if(ctx['LOCALITY_TYPE'] != null && ctx['SUBURB'] != null && ctx['LOCALITY_TYPE'] == "SUBURB" && ctx['LOCALITY_TYPE'] != ctx['CITY_LOCALITY']) {
  SUBURB_DETAIL = ctx['SUBURB'];
}          
// If Locality_type is null then use the SUBURB field.
if(ctx['LOCALITY_TYPE'] == null && ctx['SUBURB'] != null)  {
  SUBURB_DETAIL = ctx['SUBURB'];
}
// If the Locality_type is Locality then the SUBURB field should be blank
// If the locality_type is LOCALITY then use the SUBURB field otherwise use the CITY_LOCALITY
if(ctx['LOCALITY_TYPE'] != null && ctx['SUBURB'] != null && ctx['LOCALITY_TYPE'] == "LOCALITY") {
  SUBURB_DETAIL = '';
  LOCALITY_DETAIL = ctx['SUBURB'];
} else {
  LOCALITY_DETAIL = ctx['CITY_LOCALITY'];
}
// If Postcode Zone has detail
if(ctx['POSTCODE_ZONE'] != null) {
  POSTCODE_DETAIL = ctx['POSTCODE_ZONE'];
}
// UNIT_TYPE
if(ctx['UNIT_TYPE'] != null) {
  UNIT_TYPE = ctx['UNIT_TYPE'];
}
// HABITATION_NAME
if (ctx['HABITATION_NAME'] != null) {
  HABITATION_NAME = ctx['HABITATION_NAME'];
}
// Level Detail consists of Level Type and Level Number with space between and trimmed (either fied may contain data)
if (ctx['LEVEL_TYPE'] != null && ctx['LEVEL_NUMBER'] != null) {
  LEVEL_DETAIL = ctx['LEVEL_TYPE'] + ' ' + ctx['LEVEL_NUMBER'];
} 
else if (ctx['LEVEL_TYPE'] != null && ctx['LEVEL_NUMBER'] == null) {
  LEVEL_DETAIL = ctx['LEVEL_TYPE'];
}
else if (ctx['LEVEL_TYPE'] == null && ctx['LEVEL_NUMBER'] != null) {
  LEVEL_DETAIL = ctx['LEVEL_NUMBER'];
}

// If there is Unit Detail
if (UNIT_DETAIL != null) {
  ctx['address'] = UNIT_DETAIL + '/' + HOUSE_DETAIL + ' ' + STREET_DETAIL + ' ' + SUBURB_DETAIL + ' ' + LOCALITY_DETAIL + ' ' + POSTCODE_DETAIL;
} else {
  ctx['address'] = HOUSE_DETAIL + ' ' + STREET_DETAIL + ' ' + SUBURB_DETAIL + ' ' +  LOCALITY_DETAIL + ' ' + POSTCODE_DETAIL;
}
// If there is additional detail, commas to be formatted depending on what fields have data). This detail is appended to the ADDRESS_STRING
if (UNIT_TYPE != null && HABITATION_NAME == null && LEVEL_DETAIL == null) {
  ctx['address'] += ' (' + UNIT_TYPE + ')';
} 
else if (UNIT_TYPE != null && HABITATION_NAME != null && LEVEL_DETAIL == null) {
  ctx['address'] += ' (' + UNIT_TYPE + ',' + HABITATION_NAME + ')';
}
else if (UNIT_TYPE != null && HABITATION_NAME != null && LEVEL_DETAIL != null) {
  ctx['address'] += ' (' + UNIT_TYPE + ',' + HABITATION_NAME + ',' + LEVEL_DETAIL + ')';
}
if (UNIT_TYPE == null && HABITATION_NAME != null && LEVEL_DETAIL == null) {
  ctx['address'] += ' (' + HABITATION_NAME + ')';
} 
else if (UNIT_TYPE == null && HABITATION_NAME != null && LEVEL_DETAIL != null) {
  ctx['address'] += ' (' + HABITATION_NAME + ',' + LEVEL_DETAIL + ')';
}
if (UNIT_TYPE == null && HABITATION_NAME == null && LEVEL_DETAIL != null) {
  ctx['address'] += ' (' + LEVEL_DETAIL + ')';
} 
else if (UNIT_TYPE != null && HABITATION_NAME == null && LEVEL_DETAIL != null) {
  ctx['address'] += ' (' + UNIT_TYPE + ',' + LEVEL_DETAIL + ')';
}
// suggester for now
ctx['address_suggest'] = ctx['address'];
